          this.__dataToNotify;
          /** @type {Object} */
          this.__dataLinkedPaths;
          /** @type {boolean} */
          this.__dataHasPaths;
          /** @type {Object} */
          this.__dataCompoundStorage;
          /** @type {Polymer_PropertyEffects} */
          this.__dataHost;
          /** @type {!Object} */
          this.__dataTemp;
          /** @type {boolean} */
          this.__dataClientsInitialized;
          /** @type {!Object} */
          this.__data;
          /** @type {!Object|null} */
          this.__dataPending;
          /** @type {!Object} */
          this.__dataOld;
          /** @type {Object} */
          this.__computeEffects;
          /** @type {Object} */
          this.__reflectEffects;
          /** @type {Object} */
          this.__notifyEffects;
          /** @type {Object} */
          this.__propagateEffects;
          /** @type {Object} */
          this.__observeEffects;
          /** @type {Object} */
